mapper.update:
update student



              student_name = #{student_name},


              student_phone = #{student_phone},


              student_gender = #{student_gender},


              student_grade = #{student_grade},


              group_id = #{group_id},


              student_num = #{student_num},


              password = #{password}


              student_email = #{student_email}



where student_id = #{student_id}
